<template>  <div :class="menuIsActive? 'menu menu_active':'menu'">    <div class="menu__content">      <v-nav class="main-nav_menu"/>      <div class="menu__controls">        <v-button class="button_primary"># Полезное</v-button>        <v-button>Личный кабинет</v-button>      </div>    </div>  </div></template><script>import VNav from "@/components/MainNav.vue";export default {  name: 'v-menu',  components: {VNav},  props: {    menuIsActive: {      type: Boolean,      default: () => false,    }  }}</script><style lang="scss" scoped>.menu {  position: fixed;  right: 0;  top: 0;  left: 0;  bottom: 0;  display: none;  z-index: 10;  opacity: 0;  visibility: hidden;  transition: all .2s linear;  &_active {    opacity: 1;    visibility: visible;  }  &__content {    display: flex;    flex-direction: column;    position: absolute;    right: 0;    width: 350px;    height: 100%;    padding: 250px 20px 20px;    background-color: #fff;  }  &__controls {    display: flex;    flex-direction: column;    margin-top: auto;    row-gap: 10px;  }}@media screen and (max-width: 768px) {  .menu {    display: block;  }}@media screen and (max-width: 576px) {  .menu__content {    width: 100%;  }}</style>