<template>  <swiper      :modules="modules"      class="swiper"      :breakpoints="breakpoints"      :navigation="{ nextEl: '.swiper__btn_next', prevEl: '.swiper__btn_prev'}"      :pagination="{ clickable: true, el: '.swiper__pagination' }"  >    <swiper-slide class="swiper__item" v-for="swiperItem in swiperList" :key="swiperItem.id">      <img class="swiper__img" :src="swiperItem.icon" alt="">      <h3 class="swiper__title">{{ swiperItem.title }}</h3>    </swiper-slide>    <div class="swiper__control">      <button class="swiper__btn swiper__btn_prev">        <svg width="23" height="12" viewBox="0 0 23 12" fill="none" xmlns="http://www.w3.org/2000/svg">          <path              d="M16.1188 0L15.0578 1.00746L19.5578 5.2804H-0.00012207V6.70471H19.5578L15.0578 10.9776L16.1188 11.9851L22.4298 5.9935L16.1188 0.00189894V0Z"              fill="#E04E39"/>        </svg>      </button>      <div class="swiper__pagination"></div>      <button class="swiper__btn swiper__btn_next">        <svg width="23" height="12" viewBox="0 0 23 12" fill="none" xmlns="http://www.w3.org/2000/svg">          <path              d="M16.1188 0L15.0578 1.00746L19.5578 5.2804H-0.00012207V6.70471H19.5578L15.0578 10.9776L16.1188 11.9851L22.4298 5.9935L16.1188 0.00189894V0Z"              fill="#E04E39"/>        </svg>      </button>    </div>  </swiper></template><script>import {Navigation, Pagination} from 'swiper';import {Swiper, SwiperSlide} from 'swiper/vue';import 'swiper/scss';export default {  name: 'v-swiper',  components: {    Swiper,    SwiperSlide,  },  data() {    return {      swiperList: [        {id: Date.now(), title: 'Бухгалтеры', icon: require('@/assets/img/buh.svg')},        {id: Date.now(), title: 'Репетиторы', icon: require('@/assets/img/rep.svg')},        {id: Date.now(), title: 'Программисты', icon: require('@/assets/img/prog.svg')},        {id: Date.now(), title: 'Няни и сиделки', icon: require('@/assets/img/nyan.svg')},        {id: Date.now(), title: 'Учителя', icon: require('@/assets/img/prog.svg')},        {id: Date.now(), title: 'Уборщики', icon: require('@/assets/img/buh.svg')},      ],      breakpoints: {        320: {          slidesPerView: 1,          spaceBetween: 10        },        420: {          slidesPerView: 1,          spaceBetween: 20        },        500: {          slidesPerView: 2,          spaceBetween: 20        },        670: {          slidesPerView: 3,          spaceBetween: 30        },        920: {          slidesPerView: 4,          spaceBetween: 30        }      }    }  },  setup() {    return {      modules: [Navigation, Pagination],    };  },};</script><style>.swiper__item {  display: flex;  flex-direction: column;  flex-shrink: 0;  padding: 32px;  min-height: 140px;  border-radius: 5px;  background-color: #f2f2f2;}.swiper__img {  margin-bottom: 10px;  max-width: 20px;}.swiper__title {  font-size: 16px;  line-height: 24px;  font-weight: 300;}.swiper__btn {  background-color: transparent;  border: none;  cursor: pointer;}.swiper-button-disabled svg path {  fill: #000000;}.swiper__btn_prev {  transform: rotate(180deg);}.swiper__control {  display: flex;  align-items: center;  column-gap: 26px;  justify-content: center;  margin-top: 40px;}.swiper__pagination {  display: flex;  column-gap: 10px;}.swiper-pagination-bullet {  display: block;  width: 10px;  height: 10px;  border: 1px solid #58595B;  border-radius: 50%;  cursor: pointer;}.swiper-pagination-bullet-active {  background-color: #E04E39;  border-color: #E04E39;}</style>