<template>  <div :class="IsActive? 'burger burger_active': 'burger'" @click="openMenu">    <span class="burger__item"></span>  </div></template><script>export default {  name: 'v-burger',  data() {    return {      IsActive: false,    }  },  props: {    menuIsActive: Boolean,  },  methods: {    openMenu() {      this.IsActive = !this.IsActive;      this.$emit('openMenu')    }  }}</script><style lang="scss" scoped>.burger {  position: relative;  display: none;  width: 25px;  height: 20px;  overflow: hidden;  &::before {    content: '';    position: absolute;    top: 0;    left: 0;    display: block;    width: 25px;    height: 2px;    background-color: #58595B;    transition: all .2s linear;  }  &::after {    content: '';    position: absolute;    top: 18px;    left: 0;    display: block;    width: 25px;    height: 2px;    background-color: #58595B;    transition: all .2s linear;  }  &__item {    position: absolute;    top: 9px;    left: 0;    display: block;    width: 25px;    height: 2px;    background-color: #58595B;    transition: opacity .2s linear;  }  &_active {    .burger__item {      opacity: 0;    }    &::before {      top: 9px;      transform: rotate(45deg);    }    &::after {      top: 9px;      transform: rotate(-45deg);    }  }}@media screen and (max-width: 768px) {  .burger {    display: block;  }}</style>